mapper.select:
SELECT po.id, po.order_no, po.supplier_id, s.supplier_name, po.total_amount, po.status, po.applicant_id, u1.real_name AS applicant_name, po.auditor_id, u2.real_name AS auditor_name, po.audit_time, po.audit_remark, po.inbound_time, po.create_time FROM purchase_order po LEFT JOIN supplier s ON po.supplier_id = s.id LEFT JOIN sys_user u1 ON po.applicant_id = u1.id LEFT JOIN sys_user u2 ON po.auditor_id = u2.id
AND po.order_no LIKE CONCAT('%', #{orderNo}, '%')
AND po.supplier_id = #{supplierId}
AND po.status = #{status}
AND po.applicant_id = #{applicantId}
ORDER BY po.create_time DESC
